import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'https://localhost:7011/api/Account';
  private loggedIn = new BehaviorSubject<boolean>(this.hasToken());

  get isLoggedIn$() {
    return this.loggedIn.asObservable();
  }

  constructor(private http: HttpClient) { }

  login(username: string, password: string, rememberMe: boolean) {
    return this.http.post<any>(`${this.apiUrl}/Login`, { UserName: username, Password: password, RememberMe: rememberMe }).pipe(
      tap((res: any) => {
        if (res && res.token) {
          localStorage.setItem('token', res.token);
          this.loggedIn.next(true);
        }
      })
    );
  }

  register(registerData: { UserName: string; Email: string; Password: string; PasswordConfirm: string }) {
    return this.http.post<any>(`${this.apiUrl}/Register`, registerData);
  }

  logout() {
    localStorage.removeItem('token');
    this.loggedIn.next(false);
  }

  private hasToken(): boolean {
    return !!localStorage.getItem('token');
  }

  isLoggedIn(): boolean {
    // Implement logic to check if the user is logged in
    // Usually, this would check for the presence of a valid authentication token
    return !!localStorage.getItem('token');
  }
}
