import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
// Import other necessary services and types

@Component({
  selector: 'app-voyage-detail',
  templateUrl: './voyage-detail.component.html',
  styleUrls: ['./voyage-detail.component.css']
})
export class VoyageDetailComponent implements OnInit {
  locations: any[] = []; // Replace 'any' with your 'Location' type

  constructor(private route: ActivatedRoute, private router: Router) { }

  ngOnInit(): void {
    // Initialize map and other properties if needed
    // Fetch the ID from the route parameters and use it to load the voyage details
    const id = this.route.snapshot.paramMap.get('id');
    // Use the id to load the voyage details from a service
  }

  navigateToVoyageDetail(locationId: string): void {
    this.router.navigate(['/some-detail-page', locationId]); // Update the route as per your routing configuration
  }

  onVerifyLocation(query: string): void {
    // Implement location verification logic with Google Maps API
  }
}
<div class="voyage-details-container">
    <div id="map"></div>
    <div class="location-search">
      <input type="text" placeholder="Enter location" #searchBox>
      <button (click)="onVerifyLocation(searchBox.value)">✓</button>
    </div>
    <ul class="location-list">
      <li *ngFor="let location of locations">
        {{ location.name }}
        <!-- Add a click event that calls the navigateToVoyageDetail method with the location's ID -->
        <img [src]="location.imageUrl" alt="Location Image" (click)="navigateToVoyageDetail(location.id)" />
      </li>
    </ul>
</div>
