import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  username: string = '';
  email: string = '';
  password: string = '';
  confirmPassword: string = '';

  constructor(private authService: AuthService) {}

  register() {
    if (this.password === this.confirmPassword) {
      this.authService.register(this.username, this.email, this.password).subscribe(
        data => {
          // Handle successful registration here
          console.log('Registration successful', data);
        },
        error => {
          // Handle error here
          console.error('Registration failed', error);
        }
      );
    } else {
      // Handle password mismatch
      console.error('Passwords do not match');
    }
  }
}
<div class="register-container">
    <h2>Register</h2>
    <form (submit)="register()">
        <div class="form-group">
            <label for="username">Username</label>
            <input type="text" id="username" [(ngModel)]="username" name="username" placeholder="Choose a username" required>
        </div>
        <div class="form-group">
            <label for="email">Email</label>
            <input type="email" id="email" [(ngModel)]="email" name="email" placeholder="Enter your email" required>
        </div>
        <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" [(ngModel)]="password" name="password" placeholder="Create a password" required>
        </div>
        <div class="form-group">
            <label for="confirm-password">Confirm Password</label>
            <input type="password" id="confirm-password" [(ngModel)]="confirmPassword" name="confirmPassword" placeholder="Confirm your password" required>
        </div>
        <button type="submit">Register</button>
    </form>
    <a routerLink="/login">Already have an account? Login</a>
</div>
