import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router'; // Import Router

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  isLoggedIn: boolean = false;

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit() {
    // Subscribe to the observable to get the current login status
    this.authService.isLoggedIn$.subscribe((loggedIn: boolean) => {
      this.isLoggedIn = loggedIn;
    });
  }

  logout() {
    this.authService.logout();

    // Navigate to the home page (or login page) after logout
    this.router.navigate(['/login']).then(() => {
      this.isLoggedIn = false; // Update the state immediately on logout
    });
  }
}
<nav class="navbar">
    <div class="navbar-brand">TravelApp</div>
    <ul class="navbar-nav">
        <li><a routerLink="/">Home</a></li>
        <li *ngIf="!isLoggedIn"><a routerLink="/login">Login</a></li>
        <li *ngIf="!isLoggedIn"><a routerLink="/register">Register</a></li>
        <li *ngIf="isLoggedIn"><a (click)="logout()">Logout</a></li>
    </ul>
</nav>
