import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private http: HttpClient) { }

  login(username: string, password: string) {
    return this.http.post<any>('https://localhost:7011/api/Account/Login', { username, password });
  }

  register(registerData: { UserName: string; Email: string; Password: string; PasswordConfirm: string }) {
    return this.http.post<any>('https://localhost:7011/api/Account/Register', registerData);
  }
}
