import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private authService: AuthService) {}

  onLogin(): void {
    this.authService.login(this.username, this.password).subscribe({
      next: (response) => {
        // Handle successful login, store the token, etc.
        localStorage.setItem('authToken', response.token);
        // Redirect to the desired route after login
      },
      error: (err) => {
        // Handle error
        console.error('Login error:', err);
      }
    });
  }
}
<div class="login-container">
    <h2>Login</h2>
    <form (ngSubmit)="onLogin()">
        <div class="form-group">
            <label for="username">Username</label>
            <input type="text" id="username" [(ngModel)]="username" name="username" placeholder="Enter username" required>
        </div>
        <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" [(ngModel)]="password" name="password" placeholder="Enter password" required>
        </div>
        <button type="submit">Login</button>
    </form>
    <a routerLink="/register">Don't have an account? Register</a>
</div>
