import { Component, OnInit } from '@angular/core';
import { VoyageService } from '../voyage.service'; // Adjust the import path as necessary

@Component({
  selector: 'app-voyage-card-handler',
  templateUrl: './voyage-card-handler.component.html',
  styleUrls: ['./voyage-card-handler.component.css']
})
export class VoyageCardHandlerComponent implements OnInit {
  voyages: any[] = [];

  constructor(private voyageService: VoyageService) { }

  ngOnInit(): void {
    this.voyageService.getVoyageUpdateListener().subscribe(data => {
      this.voyages = data;
    });
  }
}
<div class="card-container">
    <div *ngFor="let voyage of voyages" class="voyage-card">
        <h3>{{ voyage.title }}</h3>
        <p>{{ voyage.description }}</p>
        <!-- Add more fields as needed -->
    </div>
</div>
