import { Component } from '@angular/core';
import { VoyageService } from '../voyage.service'; // Adjust the import path as necessary

@Component({
    selector: 'app-voyage-create',
    templateUrl: './voyage-create.component.html',
    styleUrls: ['./voyage-create.component.css']
})
export class VoyageCreateComponent {
    selectedFile: File | null = null;
    voyage = {
        title: '',
        description: '',
        isPublic: false
    };

    constructor(private voyageService: VoyageService) { }

    onFileSelected(event: Event) {
        const file = (event.target as HTMLInputElement).files?.[0];
        if (file) {
            this.selectedFile = file;
        }
    }

    onSubmit() {
        const formData = new FormData();
        if (this.selectedFile) {
            formData.append('image', this.selectedFile, this.selectedFile.name);
        }
        formData.append('title', this.voyage.title);
        formData.append('description', this.voyage.description);
        formData.append('isPublic', JSON.stringify(this.voyage.isPublic));

        this.voyageService.createVoyage(formData).subscribe(
            response => {
                console.log('Voyage created', response);
                // Handle successful response here
            },
            error => {
                console.error('Error creating voyage', error);
                // Handle error here
            }
        );
    }
}
<div class="container">
    <h2>Create New Voyage</h2>
    <form (ngSubmit)="onSubmit()" #voyageForm="ngForm">
        <div class="form-group">
            <label for="title">Title</label>
            <input type="text" class="form-control" id="title" required
                   [(ngModel)]="voyage.title" name="title">
        </div>

        <div class="form-group">
            <label for="description">Description</label>
            <textarea class="form-control" id="description" rows="3"
                      [(ngModel)]="voyage.description" name="description"></textarea>
        </div>

        <div class="form-group">
            <label for="image">Voyage Cover Image</label>
            <input type="file" class="form-control-file" id="image" (change)="onFileSelected($event)">
        </div>

        <div class="form-check">
            <input type="checkbox" class="form-check-input" id="isPublic"
                   [(ngModel)]="voyage.isPublic" name="isPublic">
            <label class="form-check-label" for="isPublic">Make this voyage public</label>
        </div>

        <button type="submit" class="btn btn-primary" [disabled]="!voyageForm.form.valid">Create Voyage</button>
    </form>
</div>
