import { Component, Input, Output, EventEmitter } from '@angular/core';

// Define a TypeScript interface for the voyage data (if not already defined)
export interface Voyage {
  id: number;
  title: string;
  imageUrl: string;
  // ... any other properties of a voyage
}

@Component({
  selector: 'app-voyage-card',
  templateUrl: './voyage-card.component.html',
  styleUrls: ['./voyage-card.component.css']
})
export class VoyageCardComponent {
  @Input() voyage: Voyage | null = null; // Initialize to null and use the Voyage interface
  @Output() onDelete = new EventEmitter<number>();
  @Output() onShare = new EventEmitter<number>();

  deleteVoyage(): void {
    // Confirm deletion only if voyage is defined
    if (this.voyage && confirm('Are you sure you want to delete this voyage?')) {
      this.onDelete.emit(this.voyage.id);
    }
  }

  shareVoyage(): void {
    // Trigger sharing only if voyage is defined
    if (this.voyage) {
      this.onShare.emit(this.voyage.id);
    }
  }
}
