import { Component } from '@angular/core';
import { Router } from '@angular/router'; // Import Router for navigation
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  rememberMe: boolean = false;
  errorMessage: string = ''; // For displaying login errors

  constructor(private authService: AuthService, private router: Router) {} // Inject the Router

  login(): void {
    this.authService.login(this.username, this.password, this.rememberMe).subscribe({
      next: (response) => {
        // Navigate to a different route upon successful login, e.g., dashboard
        this.router.navigate(['/dashboard']);
      },
      error: (error) => {
        // Capture and display the error message
        this.errorMessage = error.message || 'Failed to login. Please try again.';
      }
    });
  }
}
<div class="login-container">
    <h2>Login</h2>
    <form (submit)="login()">
        <div class="form-group">
            <label for="username">Username</label>
            <input type="text" id="username" [(ngModel)]="username" name="username" placeholder="Enter username" required>
        </div>
        <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" [(ngModel)]="password" name="password" placeholder="Enter password" required>
        </div>
        <div class="form-group">
            <label for="rememberMe">Remember Me</label>
            <input type="checkbox" id="rememberMe" [(ngModel)]="rememberMe" name="rememberMe">
        </div>
        <button type="submit">Login</button>
    </form>
    <a routerLink="/register">Don't have an account? Register</a>
</div>
