import { Component } from '@angular/core';
import { Router } from '@angular/router';  // Ajoutez cette ligne

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  constructor(private router: Router) { }  // Injectez Router ici

  // Méthode pour gérer le clic sur le bouton "Enregistrer"
  register() {
    this.router.navigate(['/register']);
  }

}
<div class="login-container">
  <h2>Se connecter</h2>
  <form>
    <label for="email">Entrer ton courriel:</label>
    <input type="email" id="email" name="email">
    <br>

    <label for="password">Entrer ton mot de passe:</label>
    <input type="password" id="password" name="password">
    <br>

    <button type="submit">Connecter</button>
  </form>
  <button class="register-btn" (click)="register()">Enregistrer</button>
</div>
